section.screen:
define  someobjecttype{
       object-specific variables ...
       name            template_name
       use             name_of_template_to_use
       register        [0/1]
       }

define host{
       host_name               bighost1
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      5

       name                    hosttemplate1
       }

define host{
       host_name               bighost2
       max_check_attempts      3

       use                     hosttemplate1
       }

define host{
       host_name               bighost2
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      3
       }

define host{
       host_name               bighost1
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      5

       name                    hosttemplate1
       }

define host{
       host_name               bighost2
       max_check_attempts      3

       use                     hosttemplate1

       name                    hosttemplate2
       }

define host{
       host_name               bighost3

       use                     hosttemplate2
       }

section.para:
You'll notice that the definition of host bighost3 inherits variables from the definition of host
    bighost2, which in turn inherits variables from the definition of host bighost1. Once &name-icinga;
    processes this configuration data, the resulting host definitions are equivalent to the following:

section.screen:
define host{
       host_name               bighost1
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      5
       }

define host{
       host_name               bighost2
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      3
       }

define host{
       host_name               bighost3
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      3
       }

define host{
       check_command           check-host-alive
       notification_options    d,u,r
       max_check_attempts      5

       name                    generichosttemplate

       register                0
       }

define host{
       host_name               bighost1
       address                 192.168.1.3

       use                     generichosthosttemplate
       }

define host{
       host_name               bighost2
       address                 192.168.1.4

       use                     generichosthosttemplate
       }

define host{
       _customvar1             somevalue  ; <-- Custom host variable
       _snmp_community         public  ; <-- Custom host variable

       name                    generichosttemplate

       register                0
       }

define host{
       host_name               bighost1
       address                 192.168.1.3

       use                     generichosthosttemplate
       }

define host{
       host_name               bighost1
       address                 192.168.1.3
       _customvar1             somevalue
       _snmp_community         public
       }

define host{
       event_handler           my-event-handler-command

       name                    generichosttemplate

       register                0
       }

define host{
       host_name               bighost1
       address                 192.168.1.3
       event_handler           null

       use                     generichosthosttemplate
       }

define host{
       hostgroups              all-servers

       name                    generichosttemplate

       register                0
       }

define host{
       host_name               linuxserver1
       hostgroups              +linux-servers,web-servers

       use                     generichosthosttemplate
       }

define host{
       host_name               linuxserver1
       hostgroups              all-servers,linux-servers,web-servers
       }

define host{
       name            linux-server
       contact_groups  linux-admins
       ...
       }

define hostescalation{
       host_name       linux-server
       contact_groups  +management
       ...
       }

define hostescalation{
       host_name       linux-server
       contact_groups  linux-admins,management
       ...
       }

para.screen:
# Generic host template define host{ name generic-host active_checks_enabled 1 check_interval 10 ... register 0 }
# Development web server template define host{ name development-server check_interval 15 notification_options d,u,r ... register 0 }
# Development web server define host{ use generic-host,development-server host_name devweb1 ... }

entry.screen:
# Development web server
define host{ 
      use        1,  4,  8 
      host_name  devweb1 ...
}
